class SlideLeft{constructor(t){const e="string"==typeof t?document.querySelector(t):t;e.dataset.styleLeft="style-left",slideTrans(e);observer("slide-animation",e)}}class SlideRight{constructor(t){const e="string"==typeof t?document.querySelector(t):t;e.dataset.styleRight="style-right",slideTrans(e);observer("slide-animation-right",e)}}class SlideBottom{constructor(t){const e="string"==typeof t?document.querySelector(t):t;e.dataset.styleBottom="style-bottom",slideTrans(e);observer("slide-animation-bottom",e)}}function slideTrans(t){t.classList.add("transition","opacity")}function observer(t,e){new IntersectionObserver(e=>{e.forEach(e=>{("function"!=typeof getCurrentAnimationPreference||getCurrentAnimationPreference())&&e.isIntersecting&&(e.target.classList.add(t),e.target.classList.add("bottom-line"))})}).observe(e)}